    try {
      const allRequests = await db.select().from(requests).where(eq(requests.status, "")).orderBy(asc(requests.toSlotStartDateTime));

      const slotIds = Array.from(new Set(allRequests.map(r => r.toSlotId)));

      if (slotIds.length === 0) {
        return res.json([]);
      }

      const slots = await db.select().from(classSlots).where(inArray(classSlots.id, slotIds));
      const slotMap = new Map(slots.map(s => [s.id, s]));

      const grouped: any[] = [];
      for (const slotId of slotIds) {
        const slot = slotMap.get(slotId);
        if (!slot) continue;

        const slotRequests = allRequests.filter(r => r.toSlotId === slotId);
        slotRequests.sort((a, b) => (a.createdAt?.getTime() || 0) - (b.createdAt?.getTime() || 0));

        grouped.push({
          slotId,
          slot,
          requests: slotRequests,
        });
      }

      grouped.sort((a, b) =>
        a.slot.lessonStartDateTime.getTime() - b.slot.lessonStartDateTime.getTime()
      );

      res.json(grouped);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  });

