        res.json({
          success: true,
          count: createdSlots.length,
          message: `${createdSlots.length}`,
          slots: createdSlots
        });
      } else {
        for (const classBand of data.classBands) {
          const dateTime = new Date(`${data.date}T${data.startTime}:00+09:00`);
          const slotId = `${data.date}_${data.startTime}_${classBand === "" ? "shokyu" : classBand === "" ? "chukyu" : "jokyu"}`;

