        warning: {
          DEFAULT: "hsl(38 92% 50% / <alpha-value>)",
          foreground: "hsl(38 92% 10% / <alpha-value>)",
        },
        info: {
          DEFAULT: "hsl(210 100% 45% / <alpha-value>)",
          foreground: "hsl(210 100% 98% / <alpha-value>)",
        },
        card: {
