import cron from "node-cron";
import { storage } from "./storage";
import { sendExpiredEmail, sendConfirmationEmail } from "./email-service";
import { createId } from "@paralleldrive/cuid2";
import { db } from "./db";
import { classSlots, requests, absences } from "@shared/schema";
import { eq, and, gte, lte, gt, asc } from "drizzle-orm";

async function confirmNextWaiter(slotId: string) {
  while (true) {
    const slot = await storage.getClassSlotById(slotId);
    if (!slot) {
      console.log(`[]  ${slotId} `);
      return;
    }

    const capacityMakeupUsed = slot.capacityMakeupUsed ?? 0;
    const remainingSlots = (slot.capacityLimit - slot.capacityCurrent) - capacityMakeupUsed;
    console.log(`[]  ${slotId} : ${remainingSlots}${slot.capacityLimit} - ${slot.capacityCurrent} - ${capacityMakeupUsed}`);

    if (remainingSlots < 1) {
      console.log(`[]  ${slotId} `);
      return;
    }

    const waitingRequests = await storage.getWaitingRequestsBySlotId(slotId);

    if (waitingRequests.length === 0) {
      console.log(`[]  ${slotId} `);
      return;
    }

    const nextRequest = waitingRequests[0];

    if (nextRequest.absenceId) {
      const absence = await storage.getAbsenceById(nextRequest.absenceId);

      if (absence && absence.createdAt) {
        const now = new Date();
        const tenMinutesAfterCreation = new Date(absence.createdAt);
        tenMinutesAfterCreation.setMinutes(tenMinutesAfterCreation.getMinutes() + 10);
        const isWithin10Minutes = now <= tenMinutesAfterCreation;

        if (isWithin10Minutes) {
          console.log(`[]  ${nextRequest.childName} 10ID: ${absence.id}`);
          return;
        }
      }
    }

    console.log(`[]  ${slotId} : ${nextRequest.childName} (${nextRequest.contactEmail})`);
    const declineToken = createId();

    await db.transaction(async (tx) => {
      await tx.update(requests)
        .set({
          status: "",
          declineToken: declineToken,
        })
        .where(eq(requests.id, nextRequest.id));

      await tx.update(classSlots)
        .set({
          capacityMakeupUsed: (slot.capacityMakeupUsed ?? 0) + 1,
          waitlistCount: Math.max(0, (slot.waitlistCount ?? 0) - 1),
        })
        .where(eq(classSlots.id, slotId));

      if (nextRequest.absenceId) {
        await tx.update(absences)
          .set({ makeupStatus: "MAKEUP_CONFIRMED" })
          .where(eq(absences.id, nextRequest.absenceId));
      }
    });

    if (nextRequest.contactEmail) {
      try {
        await sendConfirmationEmail(
          nextRequest.contactEmail,
          nextRequest.childName,
          slot.courseLabel,
          slot.date.toLocaleDateString('ja-JP'),
          slot.startTime,
          slot.classBand,
          declineToken
        );
        console.log(" :", nextRequest.contactEmail);
      } catch (error: any) {
        console.error(" :", error);
        console.error(":", error.message, error.response?.data);
      }
    } else {
      console.log(" :", nextRequest.childName);
    }
  }
}

export function startScheduler() {
  cron.schedule("*/10 * * * *", async () => {
    const now = new Date(new Date().toLocaleString('en-US', { timeZone: 'Asia/Tokyo' }));
    const currentHour = now.getHours();

    if (currentHour < 9 || currentHour >= 21) {
      return;
    }

    console.log("[Scheduler]  + ");

    const oneHourFromNow = new Date(now);
    oneHourFromNow.setMinutes(oneHourFromNow.getMinutes() + 60);

    const slotsToClose = await db.select().from(classSlots)
      .where(and(
        lte(classSlots.lessonStartDateTime, oneHourFromNow),
        gte(classSlots.lessonStartDateTime, now),
        gt(classSlots.waitlistCount, 0)
      ));

    for (const slot of slotsToClose) {
      const oneHourBefore = new Date(slot.lessonStartDateTime);
      oneHourBefore.setMinutes(oneHourBefore.getMinutes() - 60);

      if (now >= oneHourBefore) {
        console.log(`[Scheduler]  ${slot.id} ...`);

        const waitingRequests = await storage.getWaitingRequestsBySlotId(slot.id);

        for (const request of waitingRequests) {
          await storage.updateRequest(request.id, { status: "" });

          if (request.contactEmail) {
            try {
              await sendExpiredEmail(
                request.contactEmail,
                request.childName,
                slot.courseLabel,
                slot.date.toLocaleDateString('ja-JP'),
                slot.startTime,
                slot.classBand
              );
            } catch (error) {
              console.error(":", error);
            }
          }
        }

        await storage.updateClassSlot(slot.id, {
          waitlistCount: 0,
          lastNotifiedRequestId: null,
        });

        console.log(`[Scheduler]  ${slot.id} ${waitingRequests.length}`);
      }
    }

    console.log("[Scheduler] 10...");

    const tenMinutesAgo = new Date(now);
    tenMinutesAgo.setMinutes(tenMinutesAgo.getMinutes() - 10);

    const oldAbsences = await db.select().from(absences)
      .where(lte(absences.createdAt, tenMinutesAgo));

    console.log(`[Scheduler] 10: ${oldAbsences.length}`);

    const slotsToCheckArray: string[] = [];
    for (const absence of oldAbsences) {
      if (!slotsToCheckArray.includes(absence.originalSlotId)) {
        slotsToCheckArray.push(absence.originalSlotId);
      }
    }

    for (const slotId of slotsToCheckArray) {
      const slot = await storage.getClassSlotById(slotId);
      if (!slot) continue;

      const remainingSlots = (slot.capacityLimit - slot.capacityCurrent) - (slot.capacityMakeupUsed ?? 0);
      const waitingRequests = await storage.getWaitingRequestsBySlotId(slotId);

      if (remainingSlots > 0 && waitingRequests.length > 0) {
        console.log(`[Scheduler]  ${slotId} 10...`);

        const nextRequest = waitingRequests[0];

        if (nextRequest.absenceId) {
          const absence = await storage.getAbsenceById(nextRequest.absenceId);

          if (absence && absence.createdAt) {
            const tenMinutesAfterAbsenceCreation = new Date(absence.createdAt);
            tenMinutesAfterAbsenceCreation.setMinutes(tenMinutesAfterAbsenceCreation.getMinutes() + 10);

            if (now >= tenMinutesAfterAbsenceCreation) {
              console.log(`[Scheduler]  ${slotId}  ${nextRequest.childName} 10...`);
              await confirmNextWaiter(slotId);
            } else {
              console.log(`[Scheduler]  ${slotId} 10`);
            }
          }
        }
      }
    }

    console.log("[Scheduler] ");
  });

  console.log(" 10");
}

