import { getUncachableResendClient } from "./resend-client";

// URL
function getBaseUrl(): string {
  // REPLIT_DEPLOYMENT=1
  if (process.env.REPLIT_DEPLOYMENT === "1") {
    if (process.env.REPLIT_DOMAINS) {
      // REPLIT_DOMAINS
      const domains = process.env.REPLIT_DOMAINS.split(",");
      const domain = domains[0].trim();
      return `https://${domain}`;
    }
  }
  // URL
  if (process.env.REPLIT_DEV_DOMAIN) {
    return `https://${process.env.REPLIT_DEV_DOMAIN}`;
  }
  // 
  return "https://hamasui-yoyaku.replit.app";
}

const BASE_URL = getBaseUrl();
console.log(` BASE_URL: ${BASE_URL}`);

// 
function formatJST(date: Date, formatString: string): string {
  const jstDate = new Date(date.toLocaleString('en-US', { timeZone: 'Asia/Tokyo' }));
  const year = jstDate.getFullYear();
  const month = jstDate.getMonth() + 1;
  const day = jstDate.getDate();
  const hours = jstDate.getHours();
  const minutes = jstDate.getMinutes();

  return formatString
    .replace('yyyy', year.toString())
    .replace('M', month.toString())
    .replace('d', day.toString())
    .replace('HH', hours.toString().padStart(2, '0'))
    .replace('mm', minutes.toString().padStart(2, '0'));
}

// 
async function sendEmailWithRetry(
  sendFn: () => Promise<any>,
  emailType: string,
  recipient: string,
  maxRetries: number = 3
): Promise<void> {
  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      await sendFn();
      console.log(` ${emailType} (${attempt}):`, recipient);
      return;
    } catch (error: any) {
      console.error(` ${emailType} (${attempt}):`, error);
      console.error(":", error.message, error.response?.data);

      if (attempt === maxRetries) {
        console.error(` ${emailType} ():`, recipient);
        throw error;
      }

      // 
      const waitTime = Math.min(1000 * Math.pow(2, attempt - 1), 10000);
      console.log(` ${waitTime}ms...`);
      await new Promise(resolve => setTimeout(resolve, waitTime));
    }
  }
}

