import { useState, useEffect } from "react";
import { useRoute, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { CheckCircleIcon, XCircleIcon, Loader2, AlertCircle } from "lucide-react";

export default function DeclinePage() {
  const [, setLocation] = useLocation();
  const [, params] = useRoute("/decline/:token");
  const token = params?.token;

  const [status, setStatus] = useState<"loading" | "confirm" | "success" | "error">("loading");
  const [message, setMessage] = useState<string>("");
  const [requestInfo, setRequestInfo] = useState<any>(null);

  useEffect(() => {
    if (!token) {
      setStatus("error");
      setMessage("");
      return;
    }

    const fetchRequestInfo = async () => {
      try {
        const response = await fetch(`/api/decline/${token}/info`);

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || "");
        }

        const data = await response.json();
        setRequestInfo(data);
        setStatus("confirm");
      } catch (error: any) {
        setStatus("error");
        setMessage(error.message || "");
      }
    };

    fetchRequestInfo();
  }, [token]);

  const handleDecline = async () => {
    if (!token) return;

    setStatus("loading");
    try {
      const response = await fetch(`/api/decline/${token}`, {
        method: "POST",
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "");
      }

      const data = await response.json();
      setStatus("success");
      setMessage(data.message || "");
    } catch (error: any) {
      setStatus("error");
      setMessage(error.message || "");
    }
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="max-w-md w-full">
        <CardHeader className="text-center">
          {status === "loading" && (
            <>
              <Loader2 className="w-16 h-16 mx-auto mb-4 animate-spin text-primary" />
              <h1 className="text-2xl font-bold">...</h1>
            </>
          )}
          {status === "confirm" && (
            <>
              <AlertCircle className="w-16 h-16 mx-auto mb-4 text-amber-600" />
              <h1 className="text-2xl font-bold"></h1>
            </>
          )}
          {status === "success" && (
            <>
              <CheckCircleIcon className="w-16 h-16 mx-auto mb-4 text-green-600" />
              <h1 className="text-2xl font-bold text-green-600"></h1>
            </>
          )}
          {status === "error" && (
            <>
              <XCircleIcon className="w-16 h-16 mx-auto mb-4 text-red-600" />
              <h1 className="text-2xl font-bold text-red-600"></h1>
            </>
          )}
        </CardHeader>
        <CardContent className="space-y-4">
          {status === "confirm" && requestInfo && (
            <>
              <div className="bg-muted/50 rounded-lg p-4 space-y-2">
                <p className="text-sm text-muted-foreground"></p>
                <p className="font-semibold text-lg">{requestInfo.childName}</p>

                <p className="text-sm text-muted-foreground mt-4"></p>
                <p className="font-semibold">
                  {requestInfo.date} {requestInfo.startTime}
                </p>
                <p className="text-sm">{requestInfo.courseLabel} - {requestInfo.classBand}</p>
              </div>

              <div className="bg-amber-50 dark:bg-amber-950/20 border border-amber-200 dark:border-amber-800 rounded-lg p-4">
                <p className="text-sm text-amber-900 dark:text-amber-200">
                  <strong></strong>
                </p>
                <p className="text-sm text-amber-800 dark:text-amber-300 mt-2">


                </p>
              </div>

              <div className="flex flex-col gap-2 pt-2">
                <Button
                  onClick={handleDecline}
                  variant="destructive"
                  size="lg"
                  className="w-full"
                  data-testid="button-confirm-decline"
                >

                </Button>
                <Button
                  onClick={() => setLocation("/")}
                  variant="outline"
                  size="lg"
                  className="w-full"
                  data-testid="button-cancel"
                >

                </Button>
              </div>
            </>
          )}

          {status === "success" && (
            <>
              <p className="text-center text-lg"></p>
              <p className="text-center text-muted-foreground">

              </p>
              <Button
                onClick={() => setLocation("/")}
                className="w-full mt-4"
                data-testid="button-home"
              >

              </Button>
            </>
          )}

          {status === "error" && (
            <>
              <p className="text-center text-muted-foreground">{message}</p>
              <Button
                onClick={() => setLocation("/")}
                className="w-full mt-4"
                data-testid="button-home"
              >

              </Button>
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

